// User state to track navigation
let userState = { step: 'mainSchemes' };

// Append message to chat window
function appendMessage(text, type) {
  const messagesDiv = document.getElementById('chat-messages');
  const messageDiv = document.createElement('div');
  messageDiv.className = `message ${type}-message`;
  const lines = text.split('\n');
  lines.forEach(line => {
    if (line.trim()) { // Skip empty lines
      const p = document.createElement('p');
      p.textContent = line;
      messageDiv.appendChild(p);
    }
  });
  messagesDiv.appendChild(messageDiv);
  // Scroll to bottom after appending message
  messagesDiv.scrollTo({
    top: messagesDiv.scrollHeight,
    behavior: 'smooth'
  });
}

// Display main schemes list
function sendMainSchemesList() {
  const schemes = [
    '0. मेन्यू परत जा',
    '1. शैक्षणिक योजना',
    '2. घरकुल/पायाभूत सुविधा बाबतच्या योजना',
    '3. भटक्या जमाती क प्रवर्ग (धनगर) समाजासाठी राबविण्यात येणान्या विविध योजना',
    '4. सामाजिक योजना',
    '5. कौशल्य विकास व अर्थसाहाय्याच्या योजना'
  ].join('\n');

  const message = `नमस्कार! विभागा अंतर्गत राबविल्या जाणाऱ्या योजनांमध्ये आपले स्वागत आहे.\nकृपया एक योजना निवडा:\n\n${schemes}\n\nक्रमांकासह उत्तर द्या (उदा., 1). मेन्यूवर परत जाण्यासाठी 0 सह उत्तर द्या.`;
  appendMessage(message, 'bot');
}

// Display sub-schemes list
function sendSubSchemesList(mainScheme) {
  const subSchemesList = subSchemes[mainScheme] || [];
  if (subSchemesList.length === 0) {
    appendMessage('या श्रेणीसाठी कोणत्याही उपयोजना उपलब्ध नाहीत.\n\nमागील मेन्यूवर परत जाण्यासाठी 0 सह उत्तर द्या.', 'bot');
  } else {
    const message = `कृपया खालील योजनांमधून एक निवडा:\n\n0. मागील मेन्यू परत जा\n${subSchemesList.join('\n')}\n\nक्रमांकासह उत्तर द्या (उदा., 1). मागील मेन्यूवर परत जाण्यासाठी 0 सह उत्तर द्या.`;
    appendMessage(message, 'bot');
  }
}

// Display sub-sub-schemes list or details
function sendSubSubSchemesList(mainScheme, subScheme) {
  const subSubSchemesList = subSubSchemes[mainScheme]?.[subScheme] || [];
  if (subSubSchemesList.length === 0) {
    const details = subSchemeDetails[mainScheme]?.[subScheme] || 'कोणतेही तपशील उपलब्ध नाहीत.';
    appendMessage(`${details}\n\nमागील मेन्यूवर परत जाण्यासाठी 0 सह उत्तर द्या.`, 'bot');
  } else {
    const message = `कृपया खालील योजनांमधून एक निवडा:\n\n0. मागील मेन्यू परत जा\n${subSubSchemesList.join('\n')}\n\nक्रमांकासह उत्तर द्या (उदा., 1). मागील मेन्यूवर परत जाण्यासाठी 0 सह उत्तर द्या.`;
    appendMessage(message, 'bot');
  }
}

function sendSubSubSubSchemesList(main, sub, subsub) {
  const list = subSubSubSchemes[main]?.[sub]?.[subsub] || [];
  if (list.length === 0) {
    appendMessage('कोणतेही अधिक उपविभाग उपलब्ध नाहीत.\n\nमागील मेन्यूवर परत जाण्यासाठी 0 सह उत्तर द्या.', 'bot');
  } else {
    const message = `कृपया पुढील उपश्रेणीमधून निवडा:\n\n0. मागील मेन्यूवर परत जा\n${list.join('\n')}\n\nक्रमांकासह उत्तर द्या (उदा., 1)`;
    appendMessage(message, 'bot');
  }
}

// Handle user input
function sendMessage(event) {
  event.preventDefault(); // Prevent form submission from reloading the page
  const input = document.getElementById('user-input');
  const messageBody = input.value.trim();
  if (!messageBody) return;

  appendMessage(messageBody, 'user');
  input.value = '';
  handleInput(messageBody);
}

// Process user input based on current state
function handleInput(input) {
  if (userState.step === 'mainSchemes') {
    if (input === '0') {
      sendMainSchemesList();
    } else if (/^[1-5]$/.test(input)) {
      userState.step = 'subSchemes';
      userState.mainScheme = input;
      sendSubSchemesList(input);
    } else {
      appendMessage('कृपया 0 ते 5 मधील एक वैध क्रमांक निवडा.', 'bot');
    }
  } else if (userState.step === 'subSchemes') {
    const main = userState.mainScheme;
    const list = subSchemes[main];
    if (input === '0') {
      userState.step = 'mainSchemes';
      delete userState.mainScheme;
      sendMainSchemesList();
    } else if (/^\d+$/.test(input) && +input >= 1 && +input <= list.length) {
      userState.step = 'subSubSchemes';
      userState.subScheme = input;
      sendSubSubSchemesList(main, input);
    } else {
      appendMessage(`कृपया 0 ते ${list.length} मधील एक वैध क्रमांक निवडा.`, 'bot');
    }
  } else if (userState.step === 'subSubSchemes') {
    const { mainScheme, subScheme } = userState;
    const list = subSubSchemes[mainScheme]?.[subScheme] || [];
    if (input === '0') {
      userState.step = 'subSchemes';
      delete userState.subScheme;
      sendSubSchemesList(mainScheme);
    } else if (/^\d+$/.test(input) && +input >= 1 && +input <= list.length) {
      userState.subSubScheme = input;
      if (subSubSubSchemes[mainScheme]?.[subScheme]?.[input]) {
        userState.step = 'subSubSubSchemes';
        sendSubSubSubSchemesList(mainScheme, subScheme, input);
      } else {
        const detail = subSubSchemeDetails[mainScheme]?.[subScheme]?.[input] || 'कोणतेही तपशील उपलब्ध नाहीत.';
        appendMessage(`${detail}\n\nमागील मेन्यूवर परत जाण्यासाठी 0 सह उत्तर द्या.`, 'bot');
      }
    } else {
      appendMessage(`कृपया 0 ते ${list.length} मधील एक वैध क्रमांक निवडा.`, 'bot');
    }
  }  else if (userState.step === 'subSubSubSchemes') {
    const { mainScheme, subScheme, subSubScheme } = userState;
    const list = subSubSubSchemes[mainScheme]?.[subScheme]?.[subSubScheme] || [];
    if (input === '0') {
      userState.step = 'subSubSchemes';
      delete userState.subSubScheme;
      sendSubSubSchemesList(mainScheme, subScheme);
    } else if (/^\d+$/.test(input) && +input >= 1 && +input <= list.length) {
      const details = subSubSubSchemeDetails[mainScheme]?.[subScheme]?.[subSubScheme]?.[input] || 'तपशील लवकरच उपलब्ध होतील.\n\nमागील मेन्यूवर परत जाण्यासाठी 0 सह उत्तर द्या.';
      appendMessage(`${details}\n\nमागील मेन्यूवर परत जाण्यासाठी 0 सह उत्तर द्या.`, 'bot');
    } else {
      appendMessage(`कृपया 0 ते ${list.length} मधील एक वैध क्रमांक निवडा.`, 'bot');
    }
  }
}






// Initialize chat and set up event listeners
document.addEventListener('DOMContentLoaded', function() {
  // Display initial message
  sendMainSchemesList();

  // Add form submission listener
  const form = document.getElementById('chat-form');
  form.addEventListener('submit', sendMessage);
});
// Wait for the DOM to load
document.addEventListener('DOMContentLoaded', () => {
  // Get the form element
  const form = document.querySelector('form');

  // Function to scroll to the bottom
  function scrollToBottom() {
    // Try to find a message container (adjust ID/class as needed)
    const messageContainer = document.querySelector('#message-container') || document.body;

    // Scroll the container or page to the bottom
    if (messageContainer !== document.body) {
      messageContainer.scrollTo({
        top: messageContainer.scrollHeight,
        behavior: 'smooth'
      });
    } else {
      window.scrollTo({
        top: document.body.scrollHeight,
        behavior: 'smooth'
      });
    }
  }

  // Handle form submission
  if (form) {
    form.addEventListener('submit', (event) => {
      // Note: Remove event.preventDefault() if your form submits to a server
      // event.preventDefault(); // Uncomment for testing without server submission

      // Delay scroll to allow DOM updates (e.g., new message appended)
      setTimeout(scrollToBottom, 100);
    });
  }

  // Observe DOM changes for dynamic content (e.g., messages added via script)
  const observer = new MutationObserver(() => {
    // Scroll to bottom when new content is added
    scrollToBottom();
  });

  // Observe changes in the body or a specific container
  const targetNode = document.querySelector('#message-container') || document.body;
  observer.observe(targetNode, { childList: true, subtree: true });

  // Scroll to bottom on page load (in case messages are pre-loaded)
  window.addEventListener('load', scrollToBottom);
});

      
